import {useState, useEffect} from 'react'
import API_KEY from './keys'
//const CONTEXT_KEY = "7d28127c0fbd63c49";
const CONTEXT_KEY = process.env.REACT_APP_API_KEY; 

const useGoogleSearch = (term) => { 
  const [data, setData] = useState(null);

  useEffect(()=>{
    const fetchData = async() => {
      fetch(
        `https://cors-anywhere.herokuapp.com/https://www.googleapis.com/customsearch/v1?key=${API_KEY}&cx=${CONTEXT_KEY}&q=${term}`
      )
      .then(response => response.json())
      .then(result => {setData(result)})
    }

    fetchData();
  }, [term])

  return {data}
}

export default useGoogleSearch
