  const [messages, setMessages] = useState([
    {
      nama:'Ellen',
      image: 'https://www.biography.com/.image/ar_1:1%2Cc_fill%2Ccs_srgb%2Cg_face%2Cq_auto:good%2Cw_300/MTE4MDAzNDEwNTUyNTIyMjU0/audrey-hepburn-9335788-2-402.jpg',
      message: 'whats up'
